new Splide('.splide', {
    type: 'slide', // Tipo de transición del carrusel
    rewind: true, // Permite rebobinar el carrusel al llegar al último elemento
    perPage: 1, // Número de elementos por página
    pagination: false, // Desactivar paginación por puntos
    arrows: true, // Mostrar flechas de navegación
    gap: '16px', // Espacio entre los elementos del carrusel
    padding: {
        right: '16px', // Espacio entre el carrusel y los botones de flechas
        left: '16px'
    },
    breakpoints: {
        768: {
            perPage: 2, // Ajustar número de elementos por página en pantallas más pequeñas
        }
    }
}).mount();